sp_angular_provider.script:
function RiskMgmtPOC($http){
	this.getById = function(params, sessionId){
		return new Promise(function(resolve, reject){
			$http({
				 url: '/api/x_naci_risk_manage/riskmgmtrestapi/getPOCById',
				 method : "GET",
				 beforeSend: function(xhr){ xhr.setRequestHeader('X-UserToken', sessionId); },
				 headers: {'Content-Type': 'application/json'},
				 params: {'id': params.sys_id}
			}).then(resolve, reject);
		});
	};
	this.getList = function(params, sessionId){
		return new Promise(function(resolve, reject){
			$http({
				 url: '/api/x_naci_risk_manage/riskmgmtrestapi/getPOCList',
				 method : "GET",
				 beforeSend: function(xhr){ xhr.setRequestHeader('X-UserToken', sessionId); },
				 headers: {'Content-Type': 'application/json'},
				 params: params
			}).then(resolve, reject);
		});
	};
	this.update = function(params, sessionId){
		return new Promise(function(resolve, reject){
			$http({
				 url: '/api/x_naci_risk_manage/riskmgmtrestapi/updatePOC',
				 method : "POST",
				 beforeSend: function(xhr){ xhr.setRequestHeader('X-UserToken', sessionId); },
				 headers: {'Content-Type': 'application/json'},
				 data: JSON.stringify(params),
				 processData: false
			}).then(resolve, reject);
		});
	};
}
