sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var sys_id = request.body.data.sys_id;
	//var email_content = request.body.data.email_content;

	var gr = new GlideRecord('x_naci_risk_manage_risk_management');
	gr.addQuery('sys_id',sys_id);
	gr.query();
	gr.next();
	if(gr.getDisplayValue("stage") == 'Initial Review'){
		gr.comments = 'RM User:' + gs.getUser().getFirstName() + ' ' + gs.getUser().getLastName() + ' approved risk.';	
		gr.approval_action = 'approved';
		gr.update();

		gs.eventQueue('x_naci_risk_manage.rm_notify_rt',gr, 'Risk Team Member');
		/*
		var RT_email = "elena.kusterer@nih.gov";
		if(!email_content || email_content.trim() == ""){
			email_content = "-";
		}
		gs.eventQueue('x_naci_risk_manage.rm_notify_rt',gr, email_content, RT_email);
		*/
		return 1;
	}
	else{
		return 0;
	}
})(request, response);
