// Set the variable 'answer' to a comma-separated list of user or group sys_ids that you want the email sent to.
//answer=current.poc_id +',57d8408d6f098e001c857eeeae3ee459';
if(current.start_by_poc == "1"){
	var mem = new GlideRecord('sys_user_grmember');
    mem.addQuery('group.name', 'Risk Management RT');
    mem.query();
	var ids = [];
    while (mem.next()) {
      ids.push(mem.getElement('user.sys_id'));
    }
	answer = ids.join(',');
}
else{
	answer = current.poc_id;
}

